parameter_solvep.g = 9.8;p.T1 = T1;p.T2 = T2;p.T3 = T3;p.T4 = T4;p.T5 = T5;solution = ode45(@(t,y)dynamics(y,square(t./(t./100+0.1))*0.1,p), [0,20], [-pi/2, 0, 0,0]);t = solution.x;u = square(t./(t./100+0.1))*0.1;plot(t, solution.y)hold onplot(t,u)guess.time =t;guess.control = u;guess.state = solution.y;save guess.mat guess p%% animateap.l1 = 1;ap.l2 = 1.5;for i = 1:length(t)    clf; hold on;    animate(t(i), solution.y(:,i), ap)    drawnow; pause(0.01); end